context.value:
padding-bottom: 0px;
    padding-top: 0px;

    .bolt-card-header {
        padding-bottom: 0px;
    }

    &.no-column-headers .bolt-card-header {
        padding-bottom: $card-vertical-padding;
        border-bottom: 1px solid $neutral-8;
    }

    .bolt-card-content {
        padding: 0px;
    }

align-items: center;
    align-self: stretch;
    display: flex;
    flex-direction: row;
    margin: 0;
    padding: 0;
    position: relative;

position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    top: 0;

// Custom scrollbar styles for Firefox
    scrollbar-color: $transblack-20 transparent;
    scrollbar-width: thin;

    &.scroll-auto-hide {
        scrollbar-color: transparent transparent;
        &:hover {
            scrollbar-color: $transblack-20 transparent;
        }
    }

animation-duration: 4s;
    animation-timing-function: ease-in-out;
    animation-direction: normal;
    animation-iteration-count: infinite;
    background: linear-gradient(to right, $neutral-4 0%, $neutral-8 50%, $neutral-4 100%) 0px 0px / 90% 100% no-repeat $neutral-4;
    transition: opacity 200ms ease 0s;

animation-name: shimmer-thin;
    border-radius: 50%;
    height: 1.3em;
    margin-right: 4px;
    width: 1.3em;

animation-name: shimmer-thin;
    border-radius: 50%;
    height: 2.3em;
    margin-right: 4px;
    width: 2.3em;

overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;

// @NOTE: List break-all first since IE wont understand break-word and will use break-all.
    //      Modern browsers will apply break-word which uses improved word breaking over break-all.
    word-break: break-all;
    word-break: break-word;

padding: $tableCellLinkVerticalPadding $tableCellLinkHorizontalPadding;

    text-decoration: none;
    color: inherit;
    border-radius: 4px;

    &:focus,
    &:active {
        outline: none;
    }

    &:hover {
        color: $communication-foreground;
        background-color: $transblack-6;
    }

padding-left: $pageContentHorizontalPadding;
        padding-right: $pageContentHorizontalPadding;
        padding-bottom: $pageContentVerticalPadding;

        // full bleed for phones
        @include xsmall-screen {
            padding-left: 0;
            padding-right: 0;
        }
